import { HttpContextContract } from '@ioc:Adonis/Core/HttpContext'

/**
 * Silent auth middleware can be used as a global middleware to silent check
 * if the user is logged-in or not.
 *
 * The request continues as usual, even when the user is not logged-in.
 */
export default class SilentAuthMiddleware {
  /**
   * Handle request
   */
  public async handle ({ auth }: HttpContextContract, next: () => Promise<void>) {
    /**
     * Check if user is logged-in or not. If yes, then `ctx.auth.user` will be
     * set to the instance of the currently logged in user.
     */
    await auth.check()
    await next()
  }
}

import I18n from '@ioc:Adonis/Addons/I18n'
import { HttpContextContract } from '@ioc:Adonis/Core/HttpContext'

/**
 * The middleware detects the user language using the "Accept-language" HTTP header
 * or the "lang" query string parameter.
 *
 * Feel free to change the middleware implementation to what suits your needs. Just
 * make sure
 *
 * - You always ensure the user selected language is supported by your app.
 * - Only call "switchLocale" when the detected language is valid string value and
 *   not "null" or "undefined"
 */
export default class DetectUserLocale {
  /**
   * Detect user language using "Accept-language" header or
   * the "lang" query string parameter.
   *
   * The user language must be part of the "supportedLocales", otherwise
   * this method should return null.
   */
  protected getUserLanguage (ctx: HttpContextContract) {
    const availableLocales = I18n.supportedLocales()
    return ctx.request.language(availableLocales) || ctx.request.input('lang')
  }

  /**
   * Handle method is called by AdonisJS automatically on every middleware
   * class.
   */
  public async handle (ctx: HttpContextContract, next: () => Promise<void>) {
    const language = this.getUserLanguage(ctx)

    /**
     * Switch locale when we are able to detect the user language and it
     * is supported by the application
     */
    if (language) {
      ctx.i18n.switchLocale(language)
    }

    /**
     * Share i18n with view
     */
    if ('view' in ctx) {
      ctx.view.share({ i18n: ctx.i18n })
    }

    await next()
  }
}

import type { HttpContextContract } from '@ioc:Adonis/Core/HttpContext'
import AccessControl from 'App/Classes/AccessControl'

export default class Can {
  public async handle ({ response, auth }: HttpContextContract, next: () => Promise<void>, permissions: string[]) {
    const userACl = await AccessControl.getAcl(auth.user?.id)
    let userCan = false
    permissions.map(permission => {
      userACl.permissions.map(userPermission => {
        if (userPermission === permission) {
          userCan = true
        }
      })
    })

    if (!userCan) {
      response.unauthorized({
        errors: [
          {
            message: 'You do not have the required permission',
          },
        ],
      })
      return
    }

    await next()
  }
}

import User from 'App/Models/User'

export default class AccessControl {
  public static async getAcl (userId: number | undefined) {
    if (!userId) {
      return {roles: ['guest'], permissions: ['access.guest']}
    }

    const user = await User.query()
      .preload('roles', query => query.preload('permissions'))
      .where('id', userId)
      .first()

    const roles: string[] = []
    const permissions: string[] = []

    user?.roles.map(role => {
      roles.push(role.slug)
      role.permissions.map(permission => {
        permissions.push(permission.slug)
      })
    })

    return {roles: [...roles], permissions: [...new Set(permissions)]}
  }
}

import { DateTime } from 'luxon'
import Hash from '@ioc:Adonis/Core/Hash'
import {
  column,
  beforeSave,
  BaseModel,
  manyToMany,
  ManyToMany,
} from '@ioc:Adonis/Lucid/Orm'
import Role from './Role'

/**
 * @swagger
 * components:
 *   schemas:
 *    User:
 *      type: object
 *      properties:
 *        id:
 *          type: number
 *        name:
 *          type: string
 *        email:
 *          type: string
 *        remember_me_token:
 *          type: string
 *        created_at:
 *          type: string
 *        updated_at:
 *          type: string
 *
 */
export default class User extends BaseModel {
  @column({ isPrimary: true })
  public id: number

  @column()
  public email: string

  @column({ serializeAs: null })
  public password: string

  @column()
  public rememberMeToken?: string

  @column.dateTime({ autoCreate: true })
  public createdAt: DateTime

  @column.dateTime({ autoCreate: true, autoUpdate: true })
  public updatedAt: DateTime

  @column()
  public name?: string

  @beforeSave()
  public static async hashPassword (user: User) {
    if (user.$dirty.password) {
      user.password = await Hash.make(user.password)
    }
  }

  @manyToMany(() => Role)
  public roles: ManyToMany<typeof Role>
}

import { DateTime } from 'luxon'
import { BaseModel, beforeSave, column, ManyToMany, manyToMany } from '@ioc:Adonis/Lucid/Orm'
import { string } from '@ioc:Adonis/Core/Helpers'
import Permission from './Permission'

/**
 * @swagger
 * components:
 *   schemas:
 *    Role:
 *      type: object
 *      properties:
 *        id:
 *          type: number
 *        name:
 *          type: string
 *        slug:
 *          type: string
 *        description:
 *          type: string
 *        created_at:
 *          type: string
 *        updated_at:
 *          type: string
 *
 */
export default class Role extends BaseModel {
  @column({ isPrimary: true })
  public id: number

  @column()
  public name: string

  @column()
  public slug: string

  @column()
  public description: string

  @column.dateTime({ autoCreate: true })
  public createdAt: DateTime

  @column.dateTime({ autoCreate: true, autoUpdate: true })
  public updatedAt: DateTime

  @beforeSave()
  public static async makeSlug (role: Role) {
    if (role.$dirty.name) {
      role.slug = string.dotCase(role.name)
    }
  }

  @manyToMany(() => Permission)
  public permissions: ManyToMany<typeof Permission>
}

